perspectives.style:
@controls {
  bottom-right {
    image {
      src: "https://upload.wikimedia.org/wikipedia/en/thumb/a/ae/University_of_Auckland.svg/1280px-University_of_Auckland.svg.png";
      height: 70;
    }
  }

  bottom-left {
    color-legend {
      color {
        value: #4daf4a;
        label: "Communication";
      }

      color {
        value: #fddbc7;
        label: "Drama";
      }

      color {
        value: #377eb8;
        label: "English";
      }

      color {
        value: #cab2d6;
        label: "Media, Film and Televisions";
      }

      color {
        value: #ff7f00;
        label: "MƒÅori Studies";
      }

      color {
        value: #ffff33;
        label: "Sociology";
      }

      color {
        value: #3eaba0;
        label: "Pacific Studies";
      }

      color {
        value: #a65628;
        label: "Politics and International Relations";
      }

      color {
        value: #e41a1c;
        label: "Core courses";
      }
    }
  }

  bottom {
    filter {
      as: button;
      target: Capability, Theme, Capabilities-in-stages, Learning-Outcome, Assessment, Guide, SubGuide, Learning-outcome-description, Moreinfo;
      multiple: true;
      placeholder: "Views";

      option {
        label: "Theme";
        selector: Theme, ["Area"="Themes"];
      }

      option {
        label: "Capability";
        selector: Capability, Capabilities-in-stages, ["Area"="Capabilities"];
      }

      option {
        label: "Learning Outcome";
        selector: Learning-Outcome, ["Area"="Learning outcome"];
      }

      option {
        label: "Assessments";
        selector: Assessment, ["Area"="Assessment"];
      }

      option {
        label: "CP details";
        selector: Moreinfo;
      }


    }
  }

}

@settings {
  layout: scatter;
  layout-grid: true;
  layout-x: "column";
  layout-y: "row-.5";
  layout-x-multiplier: 2.5;
  layout-y-multiplier: 1;
  layout-guide-width: 1;
  layout-guide-color: #ccc;
  layout-guides: x(0) y(0) circle(100, 100);
  text-overflow: auto;
  element-size: 5;
  connection-curvature: 0.12;
  connection-size: 1;
  element-shape: rectangle;
  arrow-min-width: 0;
  arrow-min-height: 0;
  arrow-width: 5;
  arrow-height: 5;
  include: guide, subguide, guidecourse, subguidecourse, theme, capability, capabilities-in-stages, learning-outcome, assessment, class, moreinfo, prerequisite-connection, loop;
  quality: best;
  ignore-orphans: false;
  pointer-events: elements;
}

element["element type"="guide"] {
  text-align: center;
  font-color: #333;
  text-overflow: wrap 3;
  color: #fcfcfa;
  font-weight: bold;
}

